import { Text,View, Image } from "react-native";
import { styles } from "../assets/styles/styles";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { Button } from "react-native-paper";
import { useState, useEffect } from "react";

export default function ProfileScreen({navigation}){
    const [messageProfile, setMessageProfile] = useState("");
    const [contain, setContain] = useState(false);

    useEffect(()=>{
        userLogin()
    },[])

    const getUser = async () =>{
        let values = await AsyncStorage.getItem('keyProfile')
        return new Promise((resolve,  ) =>{
            if(values.length === 0){
                reject(new Error('No existen datos'))
            }else{
                setTimeout(()=>{
                    resolve(JSON.parse(values));
                },1000)
            }
        });
    }

    function userLogin(){
        let user = getUser()
        user
        .then((values)=>{
            setMessageProfile(`Bienvendo ${values.names}`)
            setTimeout(()=>{
                setContain(true)
            },2000)
        })
    }

    return (
    <View style = {[styles.container]}>
        <View style = {[styles.view]}>
            <Banner name="car"></Banner>
            <Text style={{fontFamily:'Arial',fontSize:30,marginTop:10}}>{messageProfile}</Text>
        </View>
        <View style = {[styles.view,{flex:4}]}>
        {contain && (
            <Text style={{fontFamily:'Arial',fontSize:20,marginTop:10}}>Que desea hacer</Text>
        )}
        {contain && (
            <Text style={{fontFamily:'Arial',fontSize:15,marginTop:10}}>En este enlace podras realizar un renta de carro</Text>
        )}
        {contain && (
            <Button
                style={{marginTop:10}}
                textColor="#0265FE"
                onPress={()=>{
                    navigation.navigate('Rent')
                }}
            >Rentar un carro</Button>
        )}
        {contain && (
            <Text style={{fontFamily:'Arial',fontSize:15,marginTop:10}}>Realiza la devolucion de tu carro</Text>
        )}
        {contain && (
            <Button
                style={{marginTop:10}}
                textColor="#0265FE"
                onPress={()=>{
                    navigation.navigate('CarReturn')
                }}
            >Devolver un carro</Button>
        )}
        {contain && (
            <Text style={{fontFamily:'Arial',fontSize:15,marginTop:10}}>Mira nuestros disponibles</Text>
        )}
        {contain && (
            <Button
                style={{marginTop:10}}
                textColor="#0265FE"
                onPress={()=>{
                    navigation.navigate('ListCar')
                }}
            >Ver Carror Disponibles</Button>
        )}
        </View>
    </View>
    );
}   

function Banner(props) {
    return (
      <Image style={{ width: '100%', height: '35%', resizeMode: 'stretch' }} source={require(`../assets/${props.name}.png`)}>
      </Image>
    );
  }
