import { Text, View } from "react-native";
import { TextInput, Button } from "react-native-paper";
import { useForm, Controller } from 'react-hook-form';
import { useState } from "react";
import { styles } from "../assets/styles/styles";

export default function CarScreen({ navigation }) {


  const URL = 'http://127.0.0.1:3600'

  const { control, handleSubmit, formState: { errors }, reset, setValue } = useForm({
    defaultValues: {
      plateNumber: '',
      brand: '',
      status: '',
      dailyvalue: ''
    }
  });

  //Funcion que obtiene los datos
  const getValuesArrayCar = async () =>{
    let values = await axios.get(`${URL}/users`);
    return new Promise((resolve, reject) =>{
      if(values.length === 0){
        reject(new Error('No existen datos'))
      }
      else{
        setTimeout(()=>{ 
          resolve(values.data);
        },1500)
      }
    });
  }

  const onSave = (data) =>{
    const {plateNumber,brand,status,dailyvalue} = data

  }
  
  return (
    <View style={[styles.container]}>
      <Controller
        control={control}
        rules={{
        required: true,
      }}
      render={({ field: { onChange, onBlur, value } }) => (
      <TextInput
        style={styles.texinput}
        activeOutlineColor="#0265FE"
        outlineColor="#919191"
        label="Nro Placa"
        mode="outlined"
        onBlur={onBlur}
        onChangeText={onChange}
        value={value}
      />
      )}
      name="plateNumber"
    />
    {errors.plateNumber && <Text style={{ color: 'red' }}>El Campo obligatorio</Text>}
      <Controller
        control={control}
        rules={{
        required: true,
      }}
      render={({ field: { onChange, onBlur, value } }) => (
      <TextInput
        style={styles.texinput}
        activeOutlineColor="#0265FE"
        outlineColor="#919191"
        label="Marca"
        mode="outlined"
        onBlur={onBlur}
        onChangeText={onChange}
        value={value}
      />
      )}
      name="brand"
    />
    {errors.brand && <Text style={{ color: 'red' }}>El Campo obligatorio</Text>}
      <Controller
        control={control}
        rules={{
        required: true,
      }}
      render={({ field: { onChange, onBlur, value } }) => (
      <TextInput
        style={styles.texinput}
        activeOutlineColor="#0265FE"
        outlineColor="#919191"
        label="Estado"
        mode="outlined"
        onBlur={onBlur}
        onChangeText={onChange}
        value={value}
      />
      )}
      name="status"
    />
    {errors.status && <Text style={{ color: 'red' }}>El Campo obligatorio</Text>}
      <Controller
        control={control}
        rules={{
        required: true,
      }}
      render={({ field: { onChange, onBlur, value } }) => (
      <TextInput
        style={styles.texinput}
        activeOutlineColor="#0265FE"
        outlineColor="#919191"
        label="Valor Diario"
        mode="outlined"
        onBlur={onBlur}
        onChangeText={onChange}
        value={value}
      />
      )}
      name="dailyvalue"
    />
    {errors.dailyvalue && <Text style={{ color: 'red' }}>El Campo obligatorio</Text>}
    <Button
      style={{ marginTop: 15, marginEnd: 10 }}
      icon="share"
      mode="contained"
      buttonColor="#0265FE"
    >Guardar Carro</Button>
    <Button
      style={{marginTop:10}}
      textColor="#0265FE"
      onPress={()=>{
        navigation.navigate('Login')
      }}
    >Cerrar Sesión</Button>
    </View>
  );
}
