import { Text, View } from "react-native";
import { TextInput, Button } from "react-native-paper";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useState } from "react";
import { styles } from "../assets/styles/styles";

export default function RentScreen({ navigation }) {
  const [plateNumber, setPlateNumber] = useState("");
  const [stateCar, setStateCar] = useState("");
  const [userName, setUserName] = useState("");
  const [rentNumber, setRentNumber] = useState("");
  const [rentDate, setRentDate] = useState("");
  const [errorMess, setErrorMess] = useState("");
  const [sw, setSw] = useState("");



  return (
    <View style={[styles.container]}>
      <Text style={{fontFamily:'Arial',fontSize:30,marginTop:10}}>Renta de Carros</Text>
      <Picker
        style={styles.picker}
        onValueChange={role => setRole(role)}
        value={role}
      >
        <Picker.Item label="Administrador" value="0" />
        <Picker.Item label="Usuario" value="1"/>
      </Picker>
      {/* <TextInput
        style={styles.texinput}
        activeOutlineColor="#0265FE"
        outlineColor="#919191"
        label="Nro Placa"
        mode="outlined"
        onChangeText={(plateNumber) => setPlateNumber(plateNumber)}
        value={plateNumber}
      />
      <TextInput
        style={styles.texinput}
        activeOutlineColor="#0265FE"
        outlineColor="#919191"
        label="Estado Placa"
        mode="outlined"
        editable={false}
        value={stateCar =='' ? 'Disponible': stateCar}
      />
      <TextInput
        style={styles.texinput}
        activeOutlineColor="#0265FE"
        outlineColor="#919191"
        label="Usuario"
        mode="outlined"
        onChangeText={(userName) => setUserName(userName)}
        value={userName}
      />
      <TextInput
        style={styles.texinput}
        activeOutlineColor="#0265FE"
        outlineColor="#919191"
        label="Nro Renta"
        mode="outlined"
        onChangeText={(rentNumber) => setRentNumber(rentNumber)}
        value={rentNumber}
      />
      <TextInput
        style={styles.texinput}
        activeOutlineColor="#0265FE"
        outlineColor="#919191"
        label="Fecha Renta"
        mode="outlined"
        onChangeText={(rentDate) => setRentDate(rentDate)}
        value={rentDate}
      /> */}
      <View style={[{flexDirection: "row", marginTop:20 }]}>
        <Button
          style={{ marginTop: 15, marginEnd: 10 }}
          icon="share"
          mode="contained"
          buttonColor="#0265FE"
          onPress={() => {
            if(sw==='1'){
              setRentCars();
            }else{
              setErrorMess('Se debe consultar una placa')
            }
          }}
        >Guardar</Button>
        <Button
          style={{ marginTop: 15 }}
          icon="repeat"
          mode="contained"
          buttonColor="#0265FE"
          onPress={loadDataCar}
        >Buscar Placa</Button>
      </View>
      <View style={[{flexDirection: "row" }]}>
      <Button
        style={{ marginTop: 15, marginEnd: 10 }}
        icon="repeat"
        mode="contained"
        buttonColor="#0265FE"
        onPress={searchRent}
      >Listar</Button>
      <Button
        style={{ marginTop: 15, marginEnd: 10 }}
        textColor="#0265FE"
        onPress={Limpiar}
      >Limpiar</Button>
      <Button
            style={{marginTop:10}}
            textColor="#0265FE"
            onPress={()=>{
                navigation.navigate('Profile')
            }}
      >Volver</Button>
      </View>
      <Text style={{fontFamily:'Arial',fontSize:15,marginTop:20,color:'red'}}>{errorMess}</Text>
    </View>
  );
}
