import { Text,View } from "react-native"
import { TextInput, Button } from "react-native-paper";
import { useForm, Controller } from 'react-hook-form';
import { styles } from "../assets/styles/styles"

export default function NewPasswordScreen({navigation}){


    const { control, handleSubmit, formState: { errors }, reset, setValue } = useForm({
        defaultValues: {
          userName: '',
        }
      });




    return (
    <View style = {styles.container}>
            <Text style={{fontFamily:'Arial',fontSize:40}}>Inicia sesi√≥n</Text>
            <Text style={{fontFamily:'Arial',fontSize:15,marginTop:10}}>Utiliza tu cuenta de RentCar</Text>


            <TextInput
                style={styles.texinput}
                activeOutlineColor="#0265FE"
                outlineColor="#919191"
                label="Usuario"
                mode="outlined"
                onChangeText={usr => setIdSearch(id)}
                value={userName}
                />
            <Controller
                control={control}
                rules={{
                  required: true,
                }}
                render={({ field: { onChange, onBlur, value } }) => (
                <TextInput
                  label="Usuario"
                  mode="outlined"
                  style={{ width:200 }}
                  onBlur={onBlur}
                  onChangeText={onChange}
                  value={value}
                />
                )}
              name="userName"
            />
            {errors.firstName && <Text style={{ color: 'red' }}>El nombre es obligatorio</Text>}
            <Button
                textColor="#0076C2"
                onPress={()=>{
                navigation.navigate('HomeTabs')
            }}
            >Volver</Button>
    </View>
    );
}
