import { Text,View } from "react-native"
import { TextInput, Button } from "react-native-paper";
import { useForm, Controller } from 'react-hook-form';
import AsyncStorage from "@react-native-async-storage/async-storage";
import { styles } from "../assets/styles/styles";
import { useState } from "react";
import axios from 'axios';
import { size } from "lodash";

export default function NewPasswordScreen({navigation}){

    const [errorMessage, setErrorMessage] = useState('')

    const URL = 'http://127.0.0.1:3600'

    const { control, handleSubmit, formState: { errors }, reset, setValue } = useForm({
        defaultValues: {
          newPassword: '',
          confirmNewpassword: ''
        }
      });
    
    //Funcion que obtiene los datos
  const getIdUser = async () =>{
    let values = await AsyncStorage.getItem('keyUsers')
    return new Promise((resolve, reject) =>{
      if(values.length === 0){
        reject(new Error('No existen datos'))
      }
      else{
        setTimeout(()=>{ 
          resolve(values);
        },1500)
      }
    });
  }

  const onUpdatePassword = (data)=>{
    const { newPassword, confirmNewpassword } = data;
    const idUser = getIdUser();
    idUser.then(async (values)=>{
      if(size(newPassword)<6){
        setErrorMessage('Se debe Ingresar una contraseña de al menos de 6 caracteres')
      }else if(newPassword !== newPassword){
        setErrorMessage('Las contraseñas no coinciden')
      }else{
        const response = await axios.put(`${URL}/users/${values.id}`,{
          password: newPassword
        })
        if(response){
          setErrorMessage('Verificando usuario')
        }
        else{
          setHasError(true);
          showMessage('No se actualizo contraseña ...')
        }
      }
    })
  }

    return (
    <View style = {styles.container}>
            <Text style={{fontFamily:'Arial',fontSize:25}}>Digita los datos</Text>
            <Controller
                control={control}
                rules={{
                  required: true,
                }}
                render={({ field: { onChange, onBlur, value } }) => (
                <TextInput
                  style={styles.texinput}
                  activeOutlineColor="#0265FE"
                  outlineColor="#919191"
                  label="Nueva Contraseña"
                  mode="outlined"
                  onBlur={onBlur}
                  onChangeText={onChange}
                  value={value}
                />
                )}
              name="newPassword"
            />
            {errors.newPassword && <Text style={{ color: 'red' }}>La contraseña es obligatoria</Text>}
            <Controller
                control={control}
                rules={{
                  required: true,
                }}
                render={({ field: { onChange, onBlur, value } }) => (
                <TextInput
                  style={styles.texinput}
                  activeOutlineColor="#0265FE"
                  outlineColor="#919191"
                  label="Confirmar Contraseña"
                  mode="outlined"
                  onBlur={onBlur}
                  onChangeText={onChange}
                  value={value}
                />
                )}
              name="confirmNewpassword"
            />
            {errors.confirmNewpassword && <Text style={{ color: 'red' }}>Este campo es obligatorio</Text>}
            <Button
              style={{ marginTop: 15, marginEnd: 10 }}
              buttonColor="#0265FE"
              icon="content-save" 
              mode="contained" 
              onPress={handleSubmit(onUpdatePassword)}
            >
              Guardar
            </Button>
            <Button
                textColor="#0076C2"
                onPress={()=>{
                navigation.navigate('Login')
                setValue('')
            }}
            >Regresar</Button>
            <Text style={{fontFamily:'Arial',fontSize:15,marginTop:20,color:'red'}}>{errorMessage}</Text>
    </View>
    );
}
